     private void btnDangNhapActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDangNhapActionPerformed
          check();
     }//GEN-LAST:event_btnDangNhapActionPerformed

     public void check() {
          if (txtTaiKhoan.getText().isEmpty()) {
               JOptionPane.showMessageDialog(this, "Vui lòng nhập tên đăng nhập");
               txtTaiKhoan.requestFocus();
          } else if (txtMatkhau.getText().isEmpty()) {
               JOptionPane.showMessageDialog(this, "Vui lòng nhập tên mật khảu");
               txtMatkhau.requestFocus();
          } else {
               if (txtTaiKhoan.getText().equalsIgnoreCase("admin") && txtMatkhau.getText().equalsIgnoreCase("123")) {
                    main f = new main();
                    f.setVisible(true);
                    this.dispose();
               } else {
                    lblSaiMK.setText("Tên đăng nhập hoặc mật khẩu không đúng");
               }
          }
     }

