     private void txtMatkhauKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtMatkhauKeyPressed
          boolean cap = Toolkit.getDefaultToolkit().getLockingKeyState(KeyEvent.VK_CAPS_LOCK);
          if (cap == true) {
               lblCap.setText("CAPSLOCK IS ON");
          } else {
               lblCap.setText("");
          }
     }//GEN-LAST:event_txtMatkhauKeyPressed

